authors.map:
function(doc) {
  for(i in doc.author_names) { emit(doc.author_names[i], null); } 
}

nonascii.map:
function(doc) {
  function containsAllAscii(str) {
    return  /^[\000-\177]*$/.test(str) ;
  }

  if(!containsAllAscii(doc.title))
  {
    emit(null, null);
  }
  else if(!containsAllAscii(doc.abstract))
  {
    emit(null, null);
  }
  else
  {
    for(i in doc.author_names) {
      if(!containsAllAscii(doc.author_names[i])) {
        emit(null, null);
        break;
       }
    }
  }
}

sources.map:
function(doc) {
  if('source_url' in doc) {
    emit(doc.source_url, null);
  }
  if('source_urls' in doc) {
    for(i in doc.source_urls) {
      emit(doc.source_urls[i], null)
    }
  }
  if('ids' in doc && 'url' in doc.ids) {
    emit(doc.ids.url, null);
  }
}

ids.map:
function(doc) {
  for(var i in doc.ids) { emit(i + ':' + doc.ids[i], null); } 
}
